const Hapi = require("hapi");
const hapiWebSocket = require("hapi-plugin-websocket");
const LeaderCache = require("./LeaderCache");
const ScoreStream = require("./ScoreStream");
const WebSocket = require("ws");
const fetch64 = require("fetch-base64");

const model = {
  totalPoints: 0,
  totalPictureCount: 0
};

const scores = new ScoreStream(
  "ws://score-gateway-scavenger-hunt-microservice.apps.summit-aws.sysdeseng.com/dashboard",
  true
);

const server = Hapi.server({
  port: 1234,
  routes: {
    cors: true
  }
});

///////////////////
//  Leaderboard  //
///////////////////

server.route({
  method: "GET",
  path: "/leaders",
  handler: (request, h) => {
    return LeaderCache.get();
  }
});

////////////////////
//  Image scores  //
////////////////////

server.route({
  method: "POST",
  path: "/images",
  config: {
    plugins: {
      websocket: {
        only: true,
        autoping: 30 * 1000,
        connect: ({ ctx, wss, ws, req, peers }) => {
          console.log(`[Server] client connected`);

          scores.addEventListener("message", msg => {
            if (ws.readyState === WebSocket.OPEN) {
              // console.log(
              //   `[Server] forwarding image score to client:\n${msg.data}`
              // );
              let data;
              try {
                data = JSON.parse(msg.data);
              } catch (e) {
                console.error(
                  `[Server] error occurred while JSON decoding: ${msg.data}`
                );
                return;
              }
              if (!data.imageURL) {
                return;
              }

              // update running totals
              model.totalPoints += data.score;
              model.totalPictureCount += 1;

              data.totalPoints = model.totalPoints;
              data.totalPictureCount = model.totalPictureCount;

              fetch64.remote(data.imageURL).then(result => {
                const encodedImage = result[1];
                console.log(
                  `[Server] image retrieved and encoded: ... ${data.imageURL.slice(
                    data.imageURL.length - 25
                  )}`
                );
                data.image = encodedImage;
                ws.send(JSON.stringify(data));
              });
            }
          });

          // send a single fabricated message after a short wait
          // setInterval(() => {
          //   if (ws.readyState === WebSocket.OPEN) {
          //     const data = {
          //       imageURL:
          //         "http://storage-aws2.sysdeseng.com:8080/v1/AUTH_gv0/images-aws2/12cd57d6-ab1e-44c9-a409-024005ad87dd_ee95d096-5e9a-4d4a-bcac-488d6436e47f_7134b78a-ad87-412c-b5d8-f323a47281ab.jpeg",
          //       score: 5,
          //       taskName: "Red Hat logo",
          //       image: `data:image/jpeg;base64,`
          //     };

          //     // update running totals
          //     model.totalPoints += data.score;
          //     model.totalPictureCount += 1;

          //     data.totalPoints = model.totalPoints;
          //     data.totalPictureCount = model.totalPictureCount;

          //     console.log("sending fake data");

          //     ws.send(JSON.stringify(data));
          //   }
          // }, 2000);
        },
        disconnect: ({ ctx }) => {
          console.log(`[Server] client disconnected`);
        }
      }
    }
  },
  handler: (request, h) => {
    return "";
  }
});

const init = async () => {
  await server.register(hapiWebSocket);
  await server.start();
  console.log(`Server running at: ${server.info.uri}`);
};

process.on("unhandledRejection", err => {
  console.log(err);
  process.exit(1);
});

init();
